targets.expr:
sum by (phase) (    
    (
        kube_pod_status_phase{}
        or
        label_replace ( kube_pod_status_ready{condition="true"}, "phase","Ready","","" )
    )
    * on (namespace, pod) group_left(node) node_namespace_pod:kube_pod_info:{node=~"$node"}
)

sum (
    label_replace(
      rate(container_cpu_usage_seconds_total{job="kubelet", image!="", container_name!=""}[5m]),
        "pod", "$1", "pod_name", "(.*)"
        )
    * on (namespace, pod) group_left node_namespace_pod:kube_pod_info:{node=~"$node"}
    )
/ 
sum (
    kube_node_status_allocatable_cpu_cores{node=~"$node"}
    )

